from OCC.wrapper import GeomAbs
from OCC.wrapper.GeomAPI import GeomAPI_PointsToBSpline
from OCC.utils.tcol import make_TColgp_Array1OfPnt


def fit_curve(points, min_degree=1, max_degree=8, continuity=GeomAbs.GeomAbs_C2,
              tolerance=1.0e-3):
    points = make_TColgp_Array1OfPnt(points)
    builder = GeomAPI_PointsToBSpline(points, min_degree, max_degree,
                                      continuity, tolerance)
    if builder.IsDone():
        return builder.Curve()
    else:
        msg = "fitting failed, check point spacing and tolerance."
        raise Exception(msg)