from OCC.wrapper.BRep import BRep_Tool_Pnt
from OCC.utils.gp import pnt_to_tup

__all__ = ["vertex_position"]


def vertex_position(shape):
    """Returns the position of a shape as a tuple

    :param OCC.TopoDS.TopoDS_Shape shape:
    :return: tuple[float]
    """
    return pnt_to_tup(BRep_Tool_Pnt(shape))
