import os

NASTRAN_VERSION = 2013
NASTRAN_DIR = os.path.join("C:/Programs/MSC.Software/MSC_Nastran",
                           str(NASTRAN_VERSION)+"1", "")
NASTRAN_PATH = os.path.join(NASTRAN_DIR, "bin", "nastranw.exe")
NASTRAN_DOC = os.path.join(NASTRAN_DIR, "msc" + str(NASTRAN_VERSION)+"1",
                           "Doc", "")


if __name__ == '__main__':
    if os.path.exists(NASTRAN_DIR):
        print("Found", NASTRAN_DIR)
    else:
        print("Not found", NASTRAN_DIR)
    if os.path.exists(NASTRAN_DIR):
        print("Found", NASTRAN_PATH)
    else:
        print("Not found", NASTRAN_PATH)
    if os.path.exists(NASTRAN_DOC):
        print("Found", NASTRAN_DOC)
    else:
        print("Not found", NASTRAN_DOC)
