__all__ = "nullcontext",

try:
    from contextlib import nullcontext
except ImportError:
    # < Py3.7
    class nullcontext(object):
        def __init__(self, enter_result=None):
            self.enter_result = enter_result

        def __enter__(self):
            return self.enter_result

        def __exit__(self, *args):
            pass
