ICN_DIR = os.path.join(PKG_DIR, "icons", "")


class _Undefined(object):
    # makes this Singleton pickable correctly
    def __reduce__(self):
        return "Undefined"

    def __repr__(self):
        return "Undefined"

    def __str__(self):
        return "Undefined"

    def __bool__(self):
        return False

    def __call__(self, *args, **kwargs):
        return self


class _Derived(object):
    def __repr__(self):
        return "derived"


