"""Geometry package"""

from parapy.core.snapshot import _SNAPSHOTREGISTER
from parapy.geom.generic import *
from parapy.geom.generic.positioning import (
    Tuple3D as _Tuple3D, _serialize_orientation, _serialize_position,
    _serialize_tuple_3D)
from parapy.geom.occ import *

_SNAPSHOTREGISTER.register_converter(Position, _serialize_position, 1)
_SNAPSHOTREGISTER.register_converter(Orientation, _serialize_orientation, 2)
_SNAPSHOTREGISTER.register_converter(Vector, _serialize_tuple_3D, 3)
_SNAPSHOTREGISTER.register_converter(Point, _serialize_tuple_3D, 4)
_SNAPSHOTREGISTER.register_converter(_Tuple3D, _serialize_tuple_3D, 5)
