"""Global variables for the parapy.geom.generic package"""

# mapping between direction key and primary direction key
KEY_MAIN_KEY = {'x': 'x', 'right': 'x', 'Vx': 'x',
                'x+': 'x',
                'y': 'y', 'rear': 'y', 'back': 'y', 'Vy': 'y',
                'y+': 'y',
                'z': 'z', 'up': 'z', 'top': 'z', 'Vz': 'z',
                'z+': 'z',
                'x_': 'x_', 'left': 'x_', 'Vx_': 'x_',
                'x-': 'x_', '-x': 'x_',
                'y_': 'y_', 'front': 'y_', 'Vy_': 'y_',
                'y-': 'y_', '-y': 'y_',
                'z_': 'z_', 'down': 'z_', 'bottom': 'z_', 'Vz_': 'z_',
                'z-': 'z_', '-z': 'z_'}

# mapping between axis and corresponding vector (used by Position)
KEY_VECTOR = {'x': 'Vx', 'y': 'Vy', 'z': 'Vz',
              'x_': 'Vx_', 'y_': 'Vy_', 'z_': 'Vz_'}

# mapping between primary direction key and vector tuple
KEY_VECTOR_TUPLE = {'x': (1, 0, 0), 'y': (0, 1, 0), 'z': (0, 0, 1),
                    'x_': (-1, 0, 0), 'y_': (0, -1, 0), 'z_': (0, 0, -1)}

# positivity of primary direction key
KEY_SIGN = {'x': 1, 'y': 1, 'z': 1, 'x_': -1, 'y_': -1, 'z_': -1}

# directedness of primary direction keys
KEY_BOOL = {'x': True, 'y': True, 'z': True,
            'x_': False, 'y_': False, 'z_': False}

# mapping from primary direction key to primary perpendicular key
KEY_PERP_KEY = {'x': 'z', 'y': 'x', 'z': 'y',
                'x_': 'z_', 'y_': 'x_', 'z_': 'y_'}

# mapping primary direction key to corresponding positive axis
KEY_AXIS_KEY = {'x': 'x', 'y': 'y', 'z': 'z',
                'x_': 'x', 'y_': 'y', 'z_': 'z'}

# mapping primary direction key to primary opposite key
KEY_OPP_KEY = {'x': 'x_', 'y': 'y_', 'z': 'z_',
               'x_': 'x', 'y_': 'y', 'z_': 'z'}

# mapping primary direction key to dimension
KEY_DIM = {'x': 'width', 'y': 'length', 'z': 'height',
           'x_': 'width', 'y_': 'length', 'z_': 'height'}

# mapping dimension to primary direction key
DIM_KEY = {'width': 'x', 'length': 'y', 'height': 'z'}

# list of all valid main direction keys
KEY_VALID = sorted(KEY_MAIN_KEY.keys())

# list of all valid dimension keys
DIM_VALID = [x for x in DIM_KEY.keys()]
