"""wx.ToggleButton with a bitmap icon."""

import wx


class ToggleBitmapButton(wx.ToggleButton):

    # API from BitmapButton
    def __init__(self, parent, id=-1, bitmap=wx.NullBitmap,
            pos=wx.DefaultPosition, size=wx.DefaultSize,
            style=wx.BU_AUTODRAW, validator=wx.DefaultValidator,
            name=wx.ButtonNameStr, **kwargs):

        super(ToggleBitmapButton, self).__init__(
            parent, id, "", pos, size, style, validator, name, **kwargs)

        self.SetBitmap(bitmap)
