# TODO: get true values for NACA 0012, NACA 0024
"""Several named CSTAirfoil instances."""

from parapy.lib.cst import CSTAirfoil

__all__ = ["Naca0012", "Naca0024", "ClarkY", "Fokker100"]

n = 100

upper_coefficients = [1., 1., 1]
lower_coefficients = [-1., -1., -1.]
Naca0012 = CSTAirfoil(lower_coefficients, upper_coefficients, n)

upper_coefficients = [1., 1., 1]
lower_coefficients = [-1., -1., -1.]
Naca0024 = CSTAirfoil(lower_coefficients, upper_coefficients, n)

lower_coefficients = [-0.1294, -0.0036, -0.0666]
upper_coefficients = [0.206, 0.2728, 0.2292]
ClarkY = CSTAirfoil(lower_coefficients, upper_coefficients, n)

upper_coefficients = [0.1700, 0.1000, 0.2468, 0.0982, 0.1242]
lower_coefficients = [-0.1498, -0.1028, -0.2655, -0.1142, -0.0574]
Fokker100 = CSTAirfoil(lower_coefficients, upper_coefficients, n)


if __name__ == '__main__':
    from parapy.gui import display
    display([Naca0012, Naca0024, ClarkY, Fokker100])
