function[y,F] = points_to_cst(Xu_in, Yu_in, k)

warning off

X_u = Xu_in'
Y_u = Yu_in'



for i=1:k

%Define optimization parameters
x0 = zeros(i,1);     %initial value of design vector x(starting vector for search process)
lb = -2*ones(i,1);    %upper bound vector of x
ub = 2*ones(i,1);     %lower bound vector of x

%perform optimization
[x,fval,exitflag] = fmincon(@(x)CST_objective(x, X_u, Y_u),x0,[],[],[],[],lb,ub);

%Store output optimized fitting-error value
F(i) = fval;

end

y = x'
end