"""Grid primitives"""

from parapy.geom.generic.positioning import Point


class MeshNode(Point):

    _tree_attrs = ["x", "y", "z", "mesh_id"]
    mesh_id = None

    def __new__(cls, x, y, z, mesh_id=None):
        return Point.__new__(cls, x, y, z)

    def __init__(self, x, y, z, mesh_id=None):
        super(MeshNode, self).__init__()
        self.mesh_id = mesh_id


if __name__ == '__main__':

    from parapy.gui import display
    node = MeshNode(1, 2, 3)
    display(node)
